class Solution {
private:
    static bool cmp(const pair<int,int>& a, const pair<int,int>& b)
    {
        return a.first < b.first || a.first==b.first && a.second<b.second;
    }
    vector<pair<int,int>> merge(vector<pair<int,int>>& a)
    {
        vector<pair<int,int>> ans;
        sort(a.begin(),a.end(),cmp);
        for(int i=0,j=-1;i<a.size();i++)
        {
            if(j<0 || a[i].first > ans[j].second)
            {
                ans.push_back(a[i]);
                j++;
            }
            else
            {
                ans[j].second = max(ans[j].second,a[i].second);
            }
        }
        return ans;
    }
    vector<pair<int,int>> findPairs(string& s, vector<string>& words)
    {
        vector<pair<int,int>> ans;
        for(auto& w:words)
        {
            int n = w.size();
            for(int i=0;(i=s.find(w,i))!=string::npos;i++)
            {
                ans.push_back({i,i+n});
            }
        }
        return ans;
    }
public:
    string addBoldTag(string s, vector<string>& words) {
        vector<pair<int,int>> ranges = findPairs(s,words);
        ranges = merge(ranges);
        for(auto it=ranges.rbegin();it!=ranges.rend();it++)
        {
            s.insert(it->second,"</b>");
            s.insert(it->first,"<b>");
        }
        return s;
    }
};
