class Solution {
public:

    bool helper(string &s, int ind, string a, string b){
        if(ind == s.length())return true;
        string sum = "";
        int len = (s[ind] == '0')?ind + 1: s.length();
        for(int i = ind; i < len; i++){
            sum.push_back(s[i]);
            if(sum == add(a, b)){
                if(helper(s, i + 1, b, sum))return true;
            }
        }
        return  false;
    }
    string add(string a, string b){
        string s = "";
        reverse(a.begin(), a.end());
        reverse(b.begin(), b.end());
        int i = 0, j = 0, carry = 0;
        while(i < a.length() && j < b.length()){
            int temp = (a[i] - '0') + (b[j] - '0') + carry;
            s.push_back(temp%10 + '0');
            carry = temp/10;
            i++, j++;
        }
        while(i < a.length()){
            int temp = (a[i] - '0') + carry;
            s.push_back(temp%10 + '0');
            carry = temp/10;
            i++;
        }
        while(j < b.length()){
            int temp = (b[j] - '0') + carry;
            s.push_back(temp%10 + '0');
            carry = temp/10;
            j++;
        }
        if(carry > 0){
            s.push_back(carry + '0');
        }
        reverse(s.begin(), s.end());
        return s;
    }
    bool isAdditiveNumber(string num) {
        if(num.length() < 3)return false;
        string sum1 = "";
        int end2 = (num[0] == '0')?1:num.length() - 2;
        for(int i = 0; i < end2; i++){
            sum1.push_back(num[i]);
            string sum2 = "";
            int end1 = (num[i + 1] == '0')?i + 2: num.length() - 1;
            for(int j = i + 1; j < end1; j++){
                sum2.push_back(num[j]);
                cout<<sum1<<" "<<sum2<<endl;
                if(helper(num, j + 1, sum1, sum2)){
                    return true;
                }
            }
        }
        return false;
    }
};
