class Solution {
public:
    string lastNonEmptyString(string s) {
        map<char, int> cnt;
        for(auto i : s){
            cnt[i]++;
        }
        int _max = 0;
        for(auto i : cnt)_max = max(_max, i.second);

        map<char, int> mp;
        int n = s.length();
        for(int i = 0; i < n; i++){
            if(cnt[s[i]] == _max)
                mp[s[i]] = i;
        }
        vector<pair<int, char>> arr;
        for(auto i : mp){
            arr.push_back({i.second, i.first});
        }
        sort(arr.begin(), arr.end());
        string ans = "";
        for(auto i : arr){
            ans.push_back(i.second);
        }
        return ans;
    }
};
