/**
 * @param {number[]} arr
 * @param {Function} fn
 * @return {number[]}
 */
var map = function(arr, fn) {
    const mappedArr = []
    for(let i = 0, len = arr.length-1; i <= len; i+=1) {
        const item = arr[i]
        const mappedItem = fn(item, i)
        mappedArr.push(mappedItem)
    }
    return mappedArr
};
