/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    void helper(TreeNode *root, vector<string> &ans, vector<int> &ds){
        if(root == NULL){
            return;
        }
        ds.push_back(root->val);
        helper(root->left, ans, ds);
        helper(root->right, ans, ds);

        if(!root->left && !root->right){
            string s = "";
            for(int i = 0;i<ds.size();i++){
                s.append(to_string(ds[i]));
                if(i != ds.size() - 1){
                    s.append("->");
                }
            }
            ans.push_back(s);
        }
        ds.pop_back();
    }
    vector<string> binaryTreePaths(TreeNode* root) {
        vector<string> ans;
        vector<int> ds;
        helper(root, ans, ds);
        return ans;
    }
};
