/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    int helper(TreeNode *root, unordered_map<TreeNode *,int>  &umap){
        if(root == NULL) return 0;
        return umap[root] = helper(root->left, umap) + root->val + helper(root->right, umap);
    }
    int helper2(TreeNode *root, unordered_map<TreeNode *,int>  &umap){
        if(root == NULL) return 0;
        return abs(umap[root->left] - umap[root->right])
            + helper2(root->left, umap)
            + helper2(root->right, umap);
    }
    int findTilt(TreeNode* root) {
        unordered_map<TreeNode *, int> umap;
        helper(root, umap);
        return helper2(root, umap);
    }
};
