class Solution {
public:
    int rangeBitwiseAnd(int left, int right) {
        if(left == 0)return  0;
        int a = log2(left);
        int b = log2(right);
        if(a == b){
            long ans = left;
            for(long i = left; i <= right; i++){
                ans = (ans & i);
            }
            return ans;
        }else{
            return 0;   
        }
    }
};
