class Solution {
public:
    int xorAllNums(vector<int>& nums1, vector<int>& nums2) {
        int n = nums1.size();
        int c1 = 0, c2 = 0;
        if(nums1.size()%2){
            for(auto i : nums2)c1 ^= i;
        }
        if(nums2.size()%2){
            for(auto i : nums1)c2 ^= i;
        }
        return (c1^c2);
    }
};
