class Solution {
public:
    string breakPalindrome(string s) {
        int n = s.length();
        if(n%2 == 0){
            for(int i =0 ; i < n;i++){
                if(s[i] !='a'){
                    s[i] = 'a';
                    return s;
                }
            }
            s[n-1] = 'b';
            return s;
        }else{
            if(n == 1)return "";
            int ind = -1;
            for(int i =0 ; i < n;i++){
                if(s[i] !='a'){
                    ind = i;
                    break;
                }
            }
            if(ind == -1){
                s[n-1] = 'b';
            }else if(ind == (n)/2){
                s[n-1] = 'b';
            }else{
                s[ind] = 'a';
            }
            return s;
        }
    }
};
