class Solution {
public:
    vector<vector<int>> candyCrush(vector<vector<int>>& board) {
        bool flag = false;

        int m = board.size(), n = board[0].size();

        for(int i=0;i<m;i++){
            for(int j=0;j<n-2;j++){
                int v = abs(board[i][j]);
                if(v != 0 && abs(board[i][j+1]) == v && abs(board[i][j+2]) == v){
                    flag = true;
                    //cout<<"hor "<<i<<" "<<j<<endl;
                    board[i][j] = -1*v;
                    board[i][j+1] = -1*v;
                    board[i][j+2] = -1*v;
                }
            }
        }
        //cout<<"a"<<endl;
        for(int j=0;j<n;j++){
            for(int i=0;i<m-2;i++){
                int v = abs(board[i][j]);
                if(v != 0 && abs(board[i+1][j]) == v && abs(board[i+2][j]) == v){
                    flag = true;
                    //cout<<"ver "<<i<<" "<<j<<endl;
                    board[i][j] = -1*v;
                    board[i+1][j] = -1*v;
                    board[i+2][j] = -1*v;
                }
            }
        }
        //cout<<"b"<<endl;
        for(int i=0;i<n;i++){
            int r = m-1;
            for(int j=m-1;j>=0;j--){
                if(board[j][i]>=0){
                    board[r][i] = board[j][i];
                    r--;
                }
            }

            for(int j=r;j>=0;j--){
                board[j][i] = 0;
            }
        }

        //cout<<"c"<<endl;
        if(flag)
            return candyCrush(board);
        return board;
    }
};
