class Solution {
public:
    bool canFormArray(vector<int>& arr, vector<vector<int>>& pieces) {
        map<int, int>mp;

            for(int i=0; i<arr.size(); i++){
                    mp.insert({arr[i], i});
            }

            for(auto x:pieces){
                    int n = x.size();
                    int idx = mp[x[0]];
                    int i=0; 
                    while(i<n && idx<arr.size()){

                            if(x[i] != arr[idx]) return false;
                            i++;
                            idx++;
                    }
                    if(i != n) return false;
            }
            return true;
    }
};
