/**
 * @param {number[]} nums
 * @param {number} target
 * @return {boolean}
 */
var isMajorityElement = function(nums, target) {

    let threshold = nums.length/2;
    let first = b_search_first(nums,target);

    if(first === -1) return false;

    let last = b_search_last(nums,target);
    let count = (last - first) + 1;

    if(count > threshold) return true;

    return false;

    function b_search_first(arr, target){
        let left = 0;
        let right = arr.length -1;
        let ans = -1;

        while(left <= right){
            let middle = Math.floor((left + right)/2);

            if(arr[middle] === target){
                ans = middle;
                right = middle - 1;
            }

            if(arr[middle] > target){
                right = middle - 1;
            }else if(arr[middle] < target){
                left = middle + 1;
            }
        }

        return ans;
    }

    function b_search_last(arr, target){
        let left = 0;
        let right = arr.length -1;
        let ans = -1;

        while(left <= right){
            let middle = Math.floor((left + right)/2);

            if(arr[middle] === target){
                ans = middle;
                left = middle + 1;
            }

            if(arr[middle] > target){
                right = middle - 1;
            }else if(arr[middle] < target){
                left = middle + 1;
            }
        }

        return ans;
    }
};
