class Solution {
    public boolean placeWordInCrossword(char[][] board, String word) {
        String reverseWord = new StringBuilder(word).reverse().toString();
        char[][] rotated = rotate(board);
        return helper(board, word) || helper(board, reverseWord) || 
            helper(rotated, word) || helper(rotated, reverseWord);
    }

    public boolean helper(char[][] board, String word) {
        for (char r[] : board) {
            String s[] = String.valueOf(r).split("#");
            for (String i : s) {
                if (i.length() == word.length() && isValid(i, word))
                    return true;
            }
        }
        return false;
    }

    public boolean isValid(String a, String b) {
        for (int i = 0; i<a.length(); i++) {
            if (a.charAt(i) != ' ' && a.charAt(i) != b.charAt(i))
                return false;
        }
        return true;
    }

    public char[][] rotate(char[][] board) {
        char[][] res = new char[board[0].length][board.length];
        for (int i = 0; i<board.length; i++) {
            for (int j = 0; j<board[0].length; j++) {
                res[j][i] = board[i][j];
            }
        }
        return res;
    }
}
