class Solution:
    def maxNumberOfFamilies(self, n: int, reservedSeats: List[List[int]]) -> int:
        res = 0
        rows = collections.defaultdict(list)
        for s in reservedSeats:
            rows[s[0]].append(s[1])
        res += 2 * (n - len(rows))
        for row in rows:
            seat = rows[row]
            if len(seat) == 1:
                if seat[0] in [1,10]:
                    res += 2
                else:
                    res += 1
            else:
                help = [0]*10
                for s in seat:
                    help[s-1] += 1
                temp = res
                if help[1:5] == [0,0,0,0]:
                    res += 1
                if help[5:9] == [0,0,0,0]:
                    res += 1
                if temp == res and help[3:7] == [0,0,0,0]:
                    res += 1
        return res
