class Solution {
    public Node cloneTree(Node root) {

        if(root==null) return null;

        Map<Node,Node> clone=new HashMap<>();
         clone.put(root,new Node(root.val));
        Queue<Node> q=new LinkedList<>();
        q.offer(root);

        while(!q.isEmpty()){
            Node cur=q.poll();


            for(Node nei:cur.children){
                clone.put(nei,new Node(nei.val,new ArrayList<>()));
                q.offer(nei);
                 clone.get(cur).children.add(clone.get(nei));

            }


        }

        return clone.get(root);

    }
}
