class Solution {
public:
    int sub_res(int n){

        if (n == 1)
            return 1;
        else if ( n == 2)
            return 2;
        else{
            return n * (n-1) / (n-2);
        }        
    }

    int clumsy(int n) {

        int op = 0;
        int result = n--;

        for ( int i = n; i >= 1; i-- ){

            if ( op % 4 == 0 ) {
                result *= i;
            }
            else if ( op % 4 == 1 ) {
                result /= i;
            }
            else if ( op % 4 == 2){
                result += i;
            }
            else {
                result -= sub_res(i);
                op += 2;
                i -= 2;
            }
            op++;
        }
        return result;
    }
};
