class Solution {
public:
    int longestString(int x, int y, int z) {
        string X = "AA",Y="BB",Z="AB";
        string ans;

        while(x!=0 and y!=0){
            ans+=X;
            ans+=Y;
            x--;
            y--;
        }
        int flag=-1;

        if(x!=0){
            ans+=X;
            flag=0;

        }
        if(y!=0){
            ans+=Y;
            flag=1;

        }
        int n = ans.size();
        if(ans[n-1]=='A'){
            while(z!=0){
                string temp = Z + ans;
                ans = temp;
                z--;
            }
        }
        if(ans[n-1]=='B'){
            while(z!=0){
                ans+=Z;
                z--;
            }
        }
        return ans.size();

    }
};
