class Solution {
public:
    int countDays(int days, vector<vector<int>>& meetings) {
        sort(meetings.begin(), meetings.end());
        vector<vector<int>> arr;
        vector<int> p = meetings[0];
        for(int i = 1; i < meetings.size(); i++){
            if(p[1] >= meetings[i][0]){
                p[1] = max(p[1], meetings[i][1]);
            }else{
                arr.push_back(p);
                p = meetings[i];
            }
        }
        arr.push_back(p);
        for(auto i : arr){
            cout<<i[0]<<" "<<i[1]<<endl;
        }
        int ans = 0, prev = 0;
        for(int i = 0; i < arr.size(); i++){
            ans += arr[i][0] - prev - 1;
            prev = arr[i][1];
        }
        ans += days - prev;
        return ans;
    }
};
