class Solution {
public:
    int countHillValley(vector<int>& nums) {
       int count=0;
        int n=nums.size();
        for(int i=1;i<n-1;i++)
            {int leftsmall=0;
            int rightsmall=0;
            int leftbig=0;
            int rightbig=0;
             if(nums[i-1]!=nums[i])
             {for(int j=i-1;j>=0;j--)
                {
                    if(nums[i]>nums[j])
                    {
                        leftsmall=1;
                        break;
                    }
                    else 
                        if(nums[i]<nums[j])
                    {
                        leftbig=1;
                            break;
                    }


                }


             for(int k=i+1;k<n;k++)
                {
                    if(nums[i]>nums[k])
                    {
                        rightsmall=1;
                        break;
                    }
                    else 
                        if(nums[i]<nums[k])
                    {
                        rightbig=1;
                            break;
                    }


                }
             }

             if((rightbig==1 && leftbig==1) ||(leftsmall==1 && rightsmall==1))
                 count++;

        }
        return count;

    }
};
