const int mod = 1000000007;

class Solution {
    vector<int> result3, result4;
    int f3(int l) {
        if (result3.size() > l) {
            return result3[l];
        }
        if (result3.size() == 0) {
            result3.push_back(1);
            result3.push_back(1);
            result3.push_back(2);
        }
        for (int i = result3.size(); i <= l; i++) {
            long long t =
                (long long)result3[i - 1] + result3[i - 2] + result3[i - 3];
            result3.push_back(t % mod);
        }
        return result3[l];
    }
    int f4(int l) {
        if (result4.size() > l) {
            return result4[l];
        }
        if (result4.size() == 0) {
            result4.push_back(1);
            result4.push_back(1);
            result4.push_back(2);
            result4.push_back(4);
        }
        for (int i = result4.size(); i <= l; i++) {
            long long t = (long long)result4[i - 1] + result4[i - 2] +
                          result4[i - 3] + result4[i - 4];
            result4.push_back(t % mod);
        }
        return result4[l];
    }

  public:
    int countTexts(string pressedKeys) {
        char t = 0;
        int l = 0;
        long long ret = 1;
        for (auto c : pressedKeys) {
            if (c == t) {
                l++;
            } else {
                int k = (t == '7' || t == '9') ? f4(l) : f3(l);
                ret = ret * k % mod;
                t = c;
                l = 1;
            }
        }
        int k = (t == '7' || t == '9') ? f4(l) : f3(l);
        ret = ret * k % mod;
        return ret;
    }
};
