typedef long long ll;
class Solution {
public:
    int countSubmatrices(vector<vector<int>>& grid, int k) {
        ll n = grid.size(), m = grid[0].size();
        vector<vector<ll>> pre(n + 1, vector<ll>(m + 1, 0));
        pre[0][0] = grid[0][0];

        for (ll i = 1; i < m; i++)
        pre[0][i] = pre[0][i - 1] + grid[0][i];
        for (ll i = 1; i < n; i++)
            pre[i][0] = pre[i - 1][0] + grid[i][0];
        for (ll i = 1; i < n; i++) {
            for (ll j = 1; j < m; j++)
                pre[i][j] = pre[i - 1][j] + pre[i][j - 1]
                            - pre[i - 1][j - 1] + grid[i][j];
        }
        ll ans = 0;
        for(ll i = 0; i < n; i++){
            for(ll j = 0; j < m; j++){
                if(pre[i][j] <= k){
                    ans++;
                }
            }
        }
        return ans;
    }
};
