class Solution {
public:
    vector<int> countOfPairs(int n, int x, int y) {
        vector<int> ans;
        int dist[n][n];
        for(int i = 0; i < n; i++){
            for(int j = 0; j < n; j++){
                dist[i][j] = 1e9;
            }
        }
        for(int i = 0; i < n - 1; i++){
            dist[i][i + 1] = 1;
            dist[i + 1][i] = 1;
        }
        dist[x - 1][y - 1] = 1;
        dist[y - 1][x - 1] = 1;

        for (int k = 0; k < n; k++) {
            for (int i = 0; i < n; i++) {
                for (int j = 0; j < n; j++) {
                    if (dist[i][j] > (dist[i][k] + dist[k][j])
                        && (dist[k][j] != 1e9
                            && dist[i][k] != 1e9))
                        dist[i][j] = dist[i][k] + dist[k][j];
                }
            }
        }
        // for(int i = 0; i < n; i++){
        //     for(int j = 0; j < n; j++){
        //         cout<<dist[i][j]<<" ";
        //     }
        //     cout<<endl;
        // }



        for(int i = 1; i <= n; i++){
            int res = 0;
            for(int j = 0; j < n; j++){
                for(int k = 0; k < n; k ++){
                    if(k == j)continue;
                    if(dist[j][k] == i){
                        res++;
                    }
                }
            }
            ans.push_back(res);
        }
        return ans;
    }
};
