class Solution {
public:
    int incremovableSubarrayCount(vector<int>& nums) {
        int res = 0;
        for(int i = 0; i < nums.size(); i++){
            for(int j = i ; j < nums.size(); j++){
                int f = 1, _max = -1;
                for(int k = 0; k < nums.size(); k++){
                    if(k >= i && k <= j){
                        continue;
                    }else{
                        if(nums[k] > _max){
                            _max = nums[k];
                        }else{
                            f = 0;
                        }
                    }
                }
                res += f;
            }
        }
        return res;        
    }
};
