class Solution:
    def waysToBuildRooms(self, prevRoom: List[int]) -> int:
        n = len(prevRoom)
        mod = 10**9+7

        after = [[] for _ in range(n)]
        for i in range(1,n):
            after[prevRoom[i]].append(i)

        def f(i):
            if not after:
                return 1,1
            ways,num_of_rooms=[],[]
            for j in after[i]:
                a,b = f(j)
                ways.append(a)
                num_of_rooms.append(b)
            s = sum(num_of_rooms)
            ans = 1
            for i in range(len(num_of_rooms)):
                ans*=math.comb(s,num_of_rooms[i])*ways[i]
                s-=num_of_rooms[i]
                ans%=mod
            return ans,sum(num_of_rooms)+1
        return f(0)[0]
