class Solution {
public:
    vector<int> countServers(int n, vector<vector<int>>& logs, int x, vector<int>& queries) {
        int n1 = queries.size();
        int n2 = logs.size();
        vector<pair<int,int>> v(n1),v1(n2);
        vector<int> ans(n1);
        for(int i=0;i<n1;i++){
            v[i]={queries[i],i};
        }
        for(int i=0;i<n2;i++){
            v1[i]={logs[i][1],logs[i][0]};
        }
        sort(v.begin(),v.end());
        sort(v1.begin(),v1.end());
        unordered_map<int,int> mp;
        int i=0,j=0;
        for(int k=0;k<n1;k++){
            int start = max(0,v[k].first-x);
            int end = v[k].first;
            while(j<n2 && v1[j].first<=end){
                mp[v1[j].second]++;
                j++;
            }
            while(i<n2 && v1[i].first<start){
                if(mp[v1[i].second]==1){mp.erase(v1[i].second);}
                else{mp[v1[i].second]--;}
                i++;
            }
            ans[v[k].second]=n-mp.size();
        }
        return ans;
    }
};
