class Solution {
    public void swap(int a,int b, int[]row){
        int val = row[b];
        row[b] = row[a];
        row[a] = val;
        return;
    }
    public int minSwapsCouples(int[] row) {
        int n = row.length;
        int []track = new int[n];
        int cnt = 0;
        for(int i=0;i<n;i++){
            track[row[i]] = i;
        }
        for(int i=0;i<n;i++){
            if(row[i]%2 == 0 ){
                int index = track[row[i]+1];
                if(index > i+1){
                    track[row[i+1]] = index;
                    track[row[index]] = i+1;
                    swap(i+1, index, row);
                    cnt++;
                }
            }
            else{
                int index = track[row[i]-1];
                if(index > i+1){
                    track[row[i+1]] = index;
                    track[row[index]] = i+1;
                    swap(i+1, index, row);
                    cnt++;
                }
            }
        }
        return cnt;
    }
}
