class Solution {
public:
    vector<int> leftShift(vector<int> &arr, int k){
        vector<int> temp;
        for(int i = k; i < arr.size(); i++){
            temp.push_back(arr[i]);
        }
        for(int i = 0; i < k; i++){
            temp.push_back(arr[i]);
        }
        return temp;
    }
    vector<vector<int>> rotateGrid(vector<vector<int>>& grid, int k) {
        vector<vector<int>> arr;
        int n = grid.size(), m = grid[0].size();

        for(auto i : grid){
            for(auto j : i){
                cout<<j<<" ";
            }
            cout<<endl;
        }
        cout<<endl;
        int t = min(n/2, m/2);
        for(int i = 0; i < t; i++){
            vector<int> temp;
            for(int j = i; j < m - i; j++){
                temp.push_back(grid[i][j]);
            }
            for(int j = i + 1; j < n - i; j++){
                temp.push_back(grid[j][m - i - 1]);
            }
            for(int j = m - i - 2; j >= i; j--){
                temp.push_back(grid[n - i - 1][j]);
            }
            for(int j = n - i - 2; j > i; j--){
                temp.push_back(grid[j][i]);
            }
            arr.push_back(temp);
        }  

        for(auto &i : arr){
            i = leftShift(i, k%i.size());
        }


        vector<vector<int>> ans(n, vector<int>(m, 0));
         for(int i = 0; i <t; i++){
             int l = 0;
            for(int j = i; j < m - i; j++){
                ans[i][j] = arr[i][l++];
            }
            for(int j = i + 1; j < n - i; j++){
                ans[j][m - i - 1] = arr[i][l++];
            }
            for(int j = m - i - 2; j >= i; j--){
                ans[n - i - 1][j] = arr[i][l++];
            }
            for(int j = n - i - 2; j > i; j--){
                ans[j][i] = arr[i][l++];
            }
            //  for(auto i : ans){
            //     for(auto j : i){
            //         cout<<j<<" ";
            //     }
            //     cout<<endl;
            // }
            // cout<<endl;
        }


        // cout<<endl;
        // for(auto i : ans){
        //     for(auto j : i){
        //         cout<<j<<" ";
        //     }
        //     cout<<endl;
        // }
        return ans;
    }
};
