class Solution:
    def search(self, lens, s, e, k):
        m = (s + e) // 2
        if k >= lens[m] and (m + 1 == len(lens) or k < lens[m+1]):
            return m
        elif k > lens[m]:
            return self.search(lens, m + 1, e, k)
        else:
            return self.search(lens, s, m - 1, k)

    def decodeAtIndex(self, s: str, k: int) -> str:
        lens = [0] * len(s)
        nums = set("1234567890")
        l = i = 0
        while i < len(s):
            if s[i] in nums:
                n = int(s[i])
                l *= n
            else:
                l += 1
            lens[i] = l
            if lens[i] >= k:
                break
            i += 1

        if lens[i] > k:
            i -= 1

        while k % lens[i]:
            k %= lens[i]
            i = self.search(lens, 0, i - 1, k)

        while s[i] in nums:
            i -= 1

        return s[i]
