# Definition for a binary tree node.
# class TreeNode:
#     def __init__(self, val=0, left=None, right=None):
#         self.val = val
#         self.left = left
#         self.right = right
class Solution:
    def removeLeafNodes(self, root: Optional[TreeNode], target: int) -> Optional[TreeNode]:
        isSame = True
        def helper(root, target):
            if not root:
                return None
            elif root.left==None and root.right==None:
                if root.val == target:
                    isSame = False
                    return None
                else:
                    return root
            else:
                root.left = helper(root.left, target)
                root.right = helper(root.right, target)
                return root

        def check(root, target):
            if not root:
                return False
            elif root.left==None and root.right==None:
                if root.val == target:
                    return True
                else:
                    return False
            else:
                return check(root.left, target) or check(root.right, target) 

        resroot = root
        while check(resroot, target):
            resroot = helper(resroot, target)

        return resroot
