class Skiplist {
    int[] arr;

    public Skiplist() {
        arr = new int[20001];
    }

    public boolean search(int target) {
        if (arr[target] > 0) {
            return true;
        } else {
            return false;
        }
    }

    public void add(int num) {
        arr[num]++;
    }

    public boolean erase(int num) {
        if (arr[num] > 0) {
            arr[num]--;
            return true;
        } else {
            return false;
        }
    }
}

