class TicTacToe {
public:
    int V[101][101];
    int size;
    TicTacToe(int n) {

        size = n;
    }

    int move(int row, int col, int player) {
        cout << row << ":"<< col <<":" << player << endl;
        V[row][col] = player;
        bool allRow = true, allCol=true, allDiag=true;
        int allRowCount=0, allColCount=0, allDiagCount = 0, allDiagCount2=0;
        for(int ROW=0 ; ROW<size ; ROW++ )
        {
            if(V[ROW][col] != player) {
                allRow = false;
                break;
            }
            else if(V[ROW][col] == player) {
                allRowCount++;
            }
        }

        for(int COL=0; COL < size; COL++)
        {
            if(V[row][COL] != player) {
                allCol = false;
                break;
            }
            else if(V[row][COL] == player) {
                allColCount++;
            }
        }

        for(int ROW=0, COL=0; ROW < size; ROW++, COL++)
        {
            if(V[ROW][COL] != player) {
                allDiag=false;
                break;
            }
            else if(V[ROW][COL] == player) {
                allDiagCount++;
            }
        }

        for(int ROW=0, COL=size-1; ROW < size ; ROW++, COL--)
        {
            if(V[ROW][COL] != player) {
                allDiag=false;
                break;
            }
            else if(V[ROW][COL] == player) {
                allDiagCount2++;
            }
        }
        cout << "AllRow:" << allRow << "\tAllCol:" << allCol << "\tallDiag:"<<allDiag << endl;
        cout << "Result::" <<allRowCount << ":"<< allColCount <<":"<< allDiagCount << endl;


        if(allRowCount == size || allColCount == size || allDiagCount == size 
          || allDiagCount2 == size)
            return V[row][col];
        return 0;
    }
};

