class Solution:
    def findDiagonalOrder(self, mat: List[List[int]]) -> List[int]:
        strw = 0
        erw = len(mat)
        stcl = 0
        ecl = len(mat[0])
        l = []
        up = True
        while(strw < erw and stcl<ecl):
            if up:
                while(stcl<ecl-1 and strw > 0 ):
                    l.append(mat[strw][stcl])
                    strw-=1
                    stcl+=1
                l.append(mat[strw][stcl])
                if stcl == ecl-1:
                    strw+=1
                else:
                    stcl+=1
            else:
                while(stcl>0 and strw < erw-1 ):
                    l.append(mat[strw][stcl])
                    strw+=1
                    stcl-=1
                l.append(mat[strw][stcl])
                if strw == erw-1:
                    stcl+=1
                else:
                    strw+=1
            up = not up
        return l




