/**
 * @param {number[]} calories
 * @param {number} k
 * @param {number} lower
 * @param {number} upper
 * @return {number}
 */
var dietPlanPerformance = function(calories, k, lower, upper) {
    let stack = [];
    let current_sum = 0;
    let points = 0;
    for (let i = 0; i < k - 1; i++) {
        stack.push(calories[i]);
        current_sum += calories[i];
    }

    for (let i = k - 1; i < calories.length; i++) {
        if (k === 1) {
            current_sum = calories[i];
        }
        else {
            current_sum += calories[i];
        }
        if (current_sum < lower) {
            points--;
        }
        else if (current_sum > upper) {
            points++;
        }
        if (k !== 1) {
          current_sum -= stack.shift();
          stack.push(calories[i]);
        }
    }


    return points;
};
