class Solution {
public:
    bool check(vector<int>& nums, vector<int>& arr, int mid){
        int n = nums.size();
        int m = arr.size();
        vector<int> ans(n, 0);
        for(int i = mid; i >=0; i--){
            int ind = arr[i] - 1;
            if(ans[ind] == 0){
                ans[ind] = 1;
            }else{
                for(int j = 0; j < n; j++){
                    if(ans[j] > 0 && ans[j] <= nums[j]){
                        ans[j]++;
                        break;
                    }
                }
            }
            // cout<<mid<<endl;
            //  for(auto i : ans){
            //     cout<<i<<" ";
            // }
            // cout<<endl;
        }

        for(int j = 0; j < n; j++){
            if(ans[j] != nums[j] + 1)return false;
        }

        return true;
    }
    int earliestSecondToMarkIndices(vector<int>& nums, vector<int>& changeIndices) {
        int lo = 0, hi = changeIndices.size() - 1, ans = -2;
        while(lo <= hi){
            int mid = (lo + hi)/2;
            // cout<<mid<<endl;
            if(check(nums, changeIndices, mid)){
                ans = mid;
                hi = mid - 1;
            }else{
                lo = mid + 1;
            }
        }
        return ans + 1;
    }
};
