class Solution {
public:
    vector<int> exclusiveTime(int n, vector<string>& logs) {
        vector<int> res(n, 0);
        vector<pair<int,int>> stk;
        int size = logs.size();
        string start("start"), end("end");
        for(int i=0; i<size; ++i)
        {
            int index1 = logs[i].find_first_of(':');
            int index2 = logs[i].find_last_of(':');
            int id = stoi(logs[i].substr(0, index1));
            string str = logs[i].substr(index1+1, (index2-index1-1));
            int time = stoi(logs[i].substr(index2+1));
            if (str==start)
            {
                if (!stk.empty())
                {
                    res[stk.back().first] += (time-stk.back().second);
                }
                stk.push_back({id, time});
            }
            else
            {
                res[stk.back().first] += (time-stk.back().second+1);
                stk.pop_back();
                if (!stk.empty())
                {
                    stk.back().second = time+1;
                }
            }
        }
        return res;
    }
};
