class Solution:
    def addOperators(self, num: str, target: int):
        out = []
        def dfs(string,cum,pos,pre,num,target,n):
            if pos == n: 
                if cum == target:
                    out.append(string)
                return
            if pre is None:
                for i in range(1,2 if num[0]=='0' else n+1):
                    num_str = num[:i]
                    num_int = int(num_str)
                    dfs(num_str,num_int,i,num_int,num,target,n)
            else:
                for i in range(pos+1,pos+2 if num[pos]=='0' else n+1):
                    num_str = num[pos:i]
                    #if num_str == '05':print(string,cum,pos,pre,num,target,n)
                    num_int = int(num_str)                    
                    dfs(string+'+'+num_str,cum+num_int,i,num_int,num,target,n)
                    dfs(string+'-'+num_str,cum-num_int,i,-num_int,num,target,n)
                    dfs(string+'*'+num_str,cum-pre+pre*num_int,i,pre*num_int,num,target,n)

        dfs('',0,0,None,num,target,len(num))
        return out
