class Solution {
public:
    vector<int> goodSubsetofBinaryMatrix(vector<vector<int>>& arr) {
        int m = arr.size(), n = arr[0].size();
        for(int i = 0; i < m; i++)if(accumulate(arr[i].begin(), arr[i].end(), 0LL) == 0)return {i};
        for(int i = 0; i < m; i++){
            for(int j = i + 1; j < m; j++){
                int s = 0;
                for(int k = 0; k < n; k++){
                    s += (arr[i][k] & arr[j][k]);
                }
                // cout<<i<<" "<<j<<" "<<s<<endl;
                if(s == 0)return {i, j};
            }
        }
        return {};
    }
};
