class Solution {
public:
    bool check(vector<int> &arr1, vector<int> &arr2){
        for(int i = 0; i < 26;i++){
            if(arr1[i] < arr2[i])return false;
        }
        return true;
    }
    vector<int> findAnagrams(string s, string p) {
        vector<int> ans;
        vector<int> arr1(26, 0);
        for(auto i : p)arr1[i-'a']++;
        vector<int> arr2(26, 0);
        int i = 0, j = 0;
        while(j < s.length()){
            if(arr1 == arr2){
                ans.push_back(i);
            }
            char c = s[j];
            arr2[c - 'a']++;
            while(!check(arr1, arr2)){
                arr2[s[i] - 'a']--;
                i++;
            }
            j++;
        }
        if(arr1 == arr2){
            ans.push_back(i);
        }
        return ans;
    }
};
