class Solution {
    public String findReplaceString(String s, int[] indices, String[] sources, String[] targets) {
        StringBuilder sb = new StringBuilder(s);
        int len = s.length();
        int n = indices.length;
        Pair[] pair = new Pair[n];

        for(int i=0; i<n; i++){
            pair[i] = new Pair(indices[i],sources[i],targets[i]);
        }

        Arrays.sort(pair,(p1,p2)->{
            return p1.idx - p2.idx; 
        });

        for(int i=n-1; i>=0; i--){
            Pair p = pair[i];
            int idx = p.idx;
            int s_len = p.source.length();
            if(idx<len && idx+s_len<=len){
                if(s.substring(idx,idx+s_len).equals(p.source)){
                    sb.delete(idx,idx+s_len);
                    sb.insert(idx,p.target);
                }
            }
        }
        return sb.toString();
    }

    static class Pair{
        int idx;
        String source;
        String target;
        Pair(int idx, String source, String target){
            this.idx = idx;
            this.source = source;
            this.target = target;
        }
    }
}
