class Solution {
public:
    vector<int> beautifulIndices(string s, string a, string b, int k) {
        vector<int> arr1;
        vector<int> arr2;
        for(int i = 0; i < (int)s.length() - (int)a.length() + 1; i++){
            // cout<<"YES";
            string t = s.substr(i, (int)a.length());
            if(t == a)
                arr1.push_back(i);
        }
        // for(auto i : arr1){
        //     cout<<i<<" ";
        // }
        // cout<<endl;
        for(int i = 0; i < (int)s.length() - (int)b.length() + 1; i++){
            string t = s.substr(i, (int)b.length());
            if(t == b)
                arr2.push_back(i);
        }
        vector<int> ans;
        for(int i = 0; i < arr1.size(); i++){
            auto it1 = lower_bound(arr2.begin(), arr2.end(), arr1[i]);
            auto it2 = lower_bound(arr2.begin(), arr2.end(), arr1[i] - k);
            if(it1 != arr2.end() && (*it1 - arr1[i]) <= k){
                ans.push_back(arr1[i]);
            }else if(it2 != arr2.end() && (*it2 - arr1[i]) <= k){
                ans.push_back(arr1[i]);
            }
        }
        return ans;
    }
};
