import heapq
from typing import List


class Solution:
    def kthLargestValue(self, matrix: List[List[int]], k: int) -> int:
        m, n = len(matrix), len(matrix[0])
        tops = []

        for i in range(m):
            for j in range(1, n):
                matrix[i][j] ^= matrix[i][j - 1]

        for j in range(n):
            for i in range(1, m):
                matrix[i][j] ^= matrix[i - 1][j]

        for i in range(m):
            for j in range(n):
                heapq.heappush(tops, matrix[i][j])
                if len(tops) > k:
                    heapq.heappop(tops)
        # print(f'matrix-{matrix}')                    
        # print(f'{tops}')
        return tops[0]
