class Solution {
    public int smallestCommonElement(int[][] mat) {
        int m = mat.length;
        int n = mat[0].length;
        int[] index = new int[m];

        while(true) {
            if(index[0]>=n) {
                break;
            }

            boolean flag = true;
            for(int i=1;i<m;i++) {
                if(index[i]>=n) {
                    return -1;
                }
                if(mat[i][index[i]]<mat[0][index[0]]) {
                    index[i]++;
                    flag = false;
                } else if(mat[i][index[i]]>mat[0][index[0]]) {
                    index[0]++;
                    flag = false;
                    break;
                }
            }

            if(flag) {
                return mat[0][index[0]];
            }
        }

        return -1;
    }
}
