/* The knows API is defined for you.
      bool knows(int a, int b); */

class Solution {
public:
    int findCelebrity(int n) {
        int i = 0, j = n-1;
        while (i<n && j>=0)
        {
            if (knows(i, j))
            {
                i++;
            } else
            {
                j--;
            }
        }
        // check for j if its a valid celebrity or not
        if (j < 0) return j;
        // check if j knows none
        for (i=0; i<n; i++)
        {
            if (i != j && knows(j, i))
            {
                return -1;
            }
        }
        // check if all knows j
        int knowCount = 0;
        for (i=0; i<n; i++)
        {
            if (i!=j)
            {
                knowCount += knows(i, j);
            }
        }
        return (knowCount == n-1)?j:-1;
    }
};
