class Solution {
public:
    int helper(vector<int> &nums1, vector<int> &nums2, int ind, int ans, int left){
        int i = ind;
        int j = 0;
        while(i < nums1.size() && j < nums2.size()){
            if(nums2[j] - nums1[i] == ans){
                i++;
                j++;
            }else{
                i++;
                left--;
            }
        }
        if(left < 0)return 1e9;
        return ans;
    }
    int minimumAddedInteger(vector<int>& nums1, vector<int>& nums2) {
        sort(nums1.begin(), nums1.end());
        sort(nums2.begin(), nums2.end());
        int a = (helper(nums1, nums2, 0, nums2[0] - nums1[0], 2));
        int b = 1e9, c = 1e9;
        if(nums1.size() > 1)
            b = (helper(nums1, nums2, 1, nums2[0] - nums1[1], 1));
        if(nums1.size() > 2)
            c = (helper(nums1, nums2, 2, nums2[0] - nums1[2], 0));
        // cout<<a<<" "<<b<<" "<<c<<endl;
        return min({a, b, c});
    }
};
