class Solution {
public:
    int longestCommonPrefix(vector<int>& arr1, vector<int>& arr2) {
        int lo = 1, hi = 1e9, ans = 0;
        while(lo <= hi){
            int mid = (lo + hi +1)/2;
            map<string, int> mp;
            for(int i = 0; i < arr1.size(); i++){
                string s = to_string(arr1[i]);
                if(s.length() >= mid){
                    mp[s.substr(0, mid)] = 1;
                }
            }
            // cout<<mid<<endl;
            // for(auto i : mp){
            //     cout<<i.first<<" ";
            // }
            // cout<<endl;
            int f = 0;
            for(int i = 0; i < arr2.size(); i++){
                string s = to_string(arr2[i]);
                // cout<<mp[s.substr(0, mid)]<<": ";
                if(s.length() >= mid && mp.find(s.substr(0, mid)) != mp.end()){
                    // cout<<"YES"<<endl;
                    f = 1;
                    break;
                }
            }
            // cout<<endl;
            if(f == 1){
                ans = mid;
                lo = mid + 1;
            }else{
                hi = mid - 1;
            }
        }
        return ans;
    }
};
