class Solution(object):
    def tictactoe(self, moves):
        """
        :type moves: List[List[int]]
        :rtype: str
        """
        grid = [[0 for _ in range(3)] for _ in range(3)]
        for i, m in enumerate(moves):
            grid[m[0]][m[1]] = 1 if i % 2 == 0 else -1
        for i in range(3):
            if sum(grid[i]) == 3:
                return "A"
            if sum(grid[i]) == -3:
                return "B"
            if sum([grid[j][i] for j in range(3)]) == 3:
                return "A"
            if sum([grid[j][i] for j in range(3)]) == -3:
                return "B"
        if sum([grid[i][i] for i in range(3)]) == 3:
            return "A"
        if sum([grid[i][i] for i in range(3)]) == -3:
            return "B"
        if sum([grid[i][2 - i] for i in range(3)]) == 3:
            return "A"
        if sum([grid[i][2 - i] for i in range(3)]) == -3:
            return "B"
        if len(moves) == 9:
            return "Draw"
        return "Pending"
