class Solution:
    def generateAbbreviations(self, word: str) -> List[str]:

        abbrevs = []

        for res in self.generateAbbreviationsRec(word):

            abbrevs.append("".join([str(r) for r in res]))

        return abbrevs

    @cache
    def generateAbbreviationsRec(self, word):

        if len(word) == 1:

            return [[word], [1]]

        else:

            res = []

            for r in self.generateAbbreviationsRec(word[1:]):

                res.append([word[0]] + r)

                if isinstance(r[0], int):

                    res.append([r[0] + 1] + r[1:])

                else:

                    res.append([1] + r)

            return res
