class Solution {
    public List<List<String>> groupStrings(String[] strings) {
        //define ans,map, coner caes

        //for all strings
        //define key, diff,gen, 
            //for each char,deal diff coner case,  sae gen to value list or create new key value pair
        //save values to ans list;

        List<List<String>> ans = new ArrayList<>();
        if(strings == null || strings.length == 0) return ans;
        Map<String,List<String>> map = new HashMap<>();

        for(String s: strings){


            String key = "";
            int diff = s.charAt(0) - 'a';
            int gen = 0;

            for(char c: s.toCharArray()){

                gen = (char) (c - diff);
                if(gen < 'a') gen +=26;
                key += gen;
            }

            if(!map.containsKey(key)) map.put(key,new ArrayList<String>());
            map.get(key).add(s);

        }

        for(String key: map.keySet()) ans.add(map.get(key));

        return ans;



    }
}
