class Solution {
    public String sortString(String s) {
        char[] chars = s.toCharArray();
        char[][] cs = new char[26][500];
        int[] count = new int[26];
        for (char c : chars) {
            cs[c - 'a'][count[c - 'a']++] = c;
        }
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < 500; i++) {
            for (int j = 0; j < 26; j++) {
                if (cs[j][i] >= 'a') {
                    sb.append(cs[j][i]);
                }
            }
            i++;
            for (int j = 25; j >=0; j--) {
                if (cs[j][i] >= 'a') {
                    sb.append(cs[j][i]);
                }
            }
        }
        return sb.toString();
    }
}
