typedef long long int ll;
class Solution {
public:
    bool good(ll n, ll m, ll k, ll mid)
    {
        ll small = 0;
        for (ll i = 1; i <= n; i++)
        {
            ll l = 1, r = m, ans = 0;
            while (l <= r)
            {
                ll mid2 = (l + r + 1) / 2;
                if (i * mid2 < mid)
                {
                    l = mid2 + 1;
                    ans = mid2;
                }
                else
                {
                    r = mid2 - 1;
                }
            }
            small += ans;
        }
        return small <= k;
    }
    int findKthNumber(int m, int n, int k) {
        ll l = 1, r = n * m, ans = 0;
        while (l <= r)
        {
            ll mid = (l + r + 1) / 2;
            if (good(n, m, k - 1, mid))
            {
                l = mid + 1;
                ans = mid;
            }
            else
            {
                r = mid - 1;
            }
        }
        return ans;
    }
};
